import React, { useState } from 'react';
import './ProductInfo.css';


const ProductInfo = () => {

    const [agroProducts,setagroProducts]=useState([]);

    const [activeProduct, setActiveProduct] = useState(null);

const GetAgrochemical = async()=>{

let data  = await fetch('http://api.shreejiinternationals/products/organic');
data = await data.json();

if(data.length==0)

setagroProducts(data);

};


    const toggleProductInfo = (productId) => {
        setActiveProduct(activeProduct === productId ? null : productId);
    };



    const renderProducts = (category) => (

        agroProducts.map((product,index) => (
            <div className="product-container" key={product._id}>
                <div
                    className={`product-item ${activeProduct === product._id ? 'expanded' : ''}`}
                >
                    <div
                        className="product-title"
                        onClick={() => toggleProductInfo(product._id)}
                    >
                        {product.name}
                    </div>
                    {activeProduct === product.id && (
                        <div className="product-info">
                            <table>
                                <tbody>
                                    <tr>
                                        <th>Technical Name</th>
                                        <td>{product.TechnicalName}</td>
                                    </tr>
                                    <tr>
                                        <th>CAS No.</th>
                                        <td>{product.CASno}</td>
                                    </tr>
                                    <tr>
                                        <th>Chemical Structure</th>
                                        <td><img src={product.ChemicalStructure} alt="Chemical Structure" /></td>
                                    </tr>
                                    <tr>
                                        <th>Packing</th>
                                        <td>{product.Packing}</td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            </div>
        ))
    );


    return (

        <div className="product-info-container">
            <header className="product-header">
                <div className="cover-image">
                    <div className="cover-text">
                        <h1>Our Products</h1>
                        <p>Quality Products for Quality Produce</p>
                    </div>
                </div>
            </header>
            <div className="product-section">
                <h2>Agrochemicals</h2>
                <div className="product-list">
                    {renderProducts('agroChemical')}
                </div>
            </div>
            <div className="product-section">
                <h2>Organic</h2>
                <div className="product-list">
                    {renderProducts('organic')}
                </div>
            </div>
            <div className="product-section">
                <h2>Inorganic</h2>
                <div className="product-list">
                    {renderProducts('inorganic')}
                </div>
            </div>
        </div>
    );
};

export default ProductInfo;
