import React, { useState } from 'react';
import './ProductInfo.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import organic from './organic.jpg';
import inorganic from './inorganic.jpg';
import agro from './agro.webp';

const products = [
  { id: 1, name: 'Agrochemicals', info: 'Information about Agrochemicals', image: agro },
  { id: 2, name: 'Inorganic', info: 'Information about Inorganic chemicals', image: inorganic },
  { id: 3, name: 'Organic', info: 'Information about Organic chemicals', image: organic },
];

const ProductInfo = () => {
  const [activeProduct, setActiveProduct] = useState(null);

  const toggleProductInfo = (productId) => {
    setActiveProduct(activeProduct === productId ? null : productId);
  };

  return (
    <div className="products-section container-fluid">
      <div className="text-center my-4">
        <h2 className="products-title">Our Products</h2>
      </div>
      {products.map((product) => (
        <div key={product.id} className="product-item">
          <div
            className="product-header"
            onClick={() => toggleProductInfo(product.id)}
          >
            <img src={product.image} alt={product.name} className="product-image" />
            <h3>{product.name}</h3>
          </div>
          {activeProduct === product.id && (
            <div className="product-info">
              <p>{product.info}</p>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default ProductInfo;
