import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ProductInfo.css'; // Importing the custom CSS for product information

const ProductInfo = () => {
    const [products, setProducts] = useState({ agroChemical: [], organic: [], inorganic: [] });
    const [activeProduct, setActiveProduct] = useState(null); // Proper declaration of state
    const [isLoading, setIsLoading] = useState(true); // State for managing loading state

    useEffect(() => {
        const fetchProducts = async () => {
            setIsLoading(true); // Start loading
            try {
                const responses = await Promise.all([
                    axios.get('https://api.shreejiinternationals.in/products/agrochemical'),
                    axios.get('https://api.shreejiinternationals.in/products/organic'),
                    axios.get('https://api.shreejiinternationals.in/products/inorganic')
                ]);
                setProducts({
                    agroChemical: responses[0].data,
                    organic: responses[1].data,
                    inorganic: responses[2].data
                });
            } catch (error) {
                console.error('Error fetching products:', error);
            }
            setIsLoading(false); // End loading
        };

        fetchProducts();
    }, []);

    const toggleProductInfo = (_id) => {
        setActiveProduct(activeProduct === _id ? null : _id);
    };

    const renderProducts = (category) => {
        const categoryProducts = products[category];
        if (!Array.isArray(categoryProducts) || categoryProducts.length === 0) {
            return <p>No products available</p>;
        }
        return categoryProducts.map((product) => (
            <div className="product-container" key={product._id}>
                <div
                    className={`product-item ${activeProduct === product._id ? 'expanded' : ''}`}
                    onClick={() => toggleProductInfo(product._id)}
                >
                    {product.name}
                    {activeProduct === product._id && (
                        <div className="product-info">
                            <table>
                                <tbody>
                                    <tr>
                                        <th>Technical Name</th>
                                        <td>{product.TechnicalName || 'N/A'}</td>
                                    </tr>
                                    <tr>
                                        <th>CAS No.</th>
                                        <td>{product.CASno || 'N/A'}</td>
                                    </tr>
                                    <tr>
                                        <th>Chemical Structure</th>
                                        <td>
                                            <img src={product.ChemicalStructure || 'default-image-url'} alt="Chemical Structure" />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th>Packing</th>
                                        <td>{product.Packing || 'N/A'}</td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            </div>
        ));
    };

    if (isLoading) {
        return <div className="loading-container"><div className="spinner"></div>Loading...</div>;
    }

    return (
        <div className="product-info-container">
            <header className="product-header">
                <div className="cover-image">
                    <div className="cover-text">
                        <h1>Our Products</h1>
                        <p>Quality Products for Quality Produce</p>
                        <div className="brochure-download">
                            <a href="asset/images/SIO-Brochure.pdf"  target='_blank'>
                                <button>Download Brochure</button>
                            </a>
                        </div>
                    </div>
                </div>
            </header>
            <div className="product-section">
                <h2>Agrochemicals</h2>
                <div className="product-list">{renderProducts('agroChemical')}</div>
            </div>
            <div className="product-section">
                <h2>Organic</h2>
                <div className="product-list">{renderProducts('organic')}</div>
            </div>
            <div className="product-section">
                <h2>Inorganic</h2>
                <div className="product-list">{renderProducts('inorganic')}</div>
            </div>
        </div>
    );
};

export default ProductInfo;
