import React, { useState } from 'react';
import './ProductInfo.css';

const products = [
  { id: 1, name: 'Product 1', info: 'Information about Product 1' },
  { id: 2, name: 'Product 2', info: 'Information about Product 2' },
  { id: 3, name: 'Product 3', info: 'Information about Product 3' },
];

const ProductInfo = () => {
  const [activeProduct, setActiveProduct] = useState(null);

  const toggleProductInfo = (productId) => {
    setActiveProduct(activeProduct === productId ? null : productId);
  };

  return (
    <div className="product-info-container">
      {products.map((product) => (
        <div key={product.id} className="product-item">
          <div
            className="product-header"
            onClick={() => toggleProductInfo(product.id)}
          >
            {product.name}
          </div>
          {activeProduct === product.id && (
            <div className="product-info">
              <p>{product.info}</p>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default ProductInfo;
